from django.dispatch import Signal,receiverfrom django.db.models.signals import pre_save,post_savesignalAllen = Signal(providing_args=['allen'])@receiver(signalAllen)def signal_callback(sender,**kwargs):    print(sender,kwargs)    print('signal_callback called')# signalAllen.connect(signal_callback) #手动注册@receiver(pre_save)def pre_save_call(sender,**kwargs):    print('pre_save_call')@receiver(post_save)def post_save_call(sender,**kwargs):    print('post_save_call')